// Main application UI (kept as MainApp so we can expose /login and /signup routes)
const MainApp = () => {
  const [currentPage, setCurrentPage] = useState('welcome');
  const [receivedEntries, setReceivedEntries] = useState([]);
  const [givenEntries, setGivenEntries] = useState([]);
  const [darkMode, setDarkMode] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  const { logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const navigationItems = [
    { id: 'dashboard', name: 'Dashboard', icon: 'ğŸ“Š' },
    { id: 'file-upload', name: 'File Analysis', icon: 'ğŸ“' },
    { id: 'money-received', name: 'Money Received', icon: 'ğŸ’°' },
    { id: 'money-given', name: 'Money Given', icon: 'ğŸ’¸' },
    { id: 'loan-management', name: 'Loan Management', icon: 'ğŸ¦' },
    { id: 'daily-expenses', name: 'Daily Expenses', icon: 'âš¡' },
    { id: 'financial-insights', name: 'Financial Insights', icon: 'ğŸ’¡' },
    { id: 'savings-tracker', name: 'Savings Tracker', icon: 'ğŸ¯' },
    { id: 'data-visualization', name: 'Charts & Graphs', icon: 'ğŸ“ˆ' },
    { id: 'settings', name: 'Settings', icon: 'âš™ï¸' }
  ];

  const handleEnterApp = () => setCurrentPage('dashboard');

  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'welcome':
        return <WelcomePage onEnterApp={handleEnterApp} />;
      case 'dashboard':
        return <SummaryDashboard receivedEntries={receivedEntries} givenEntries={givenEntries} />;
      case 'file-upload':
        return <FileUploadAnalysis onDataExtracted={() => {}} />;
      case 'money-received':
        return <MoneyReceivedEntry onEntriesChange={setReceivedEntries} />;
      case 'money-given':
        return <MoneyGivenEntry onEntriesChange={setGivenEntries} />;
      case 'loan-management':
        return <LoanManagement />;
      case 'daily-expenses':
        return <DailyExpenses />;
      case 'financial-insights':
        return <FinancialInsights receivedEntries={receivedEntries} givenEntries={givenEntries} />;
      case 'savings-tracker':
        return <SavingsTracker receivedEntries={receivedEntries} givenEntries={givenEntries} />;
      case 'data-visualization':
        return <DataVisualization receivedEntries={receivedEntries} givenEntries={givenEntries} />;
      case 'settings':
        return <Settings darkMode={darkMode} setDarkMode={setDarkMode} />;
      default:
        return <WelcomePage onEnterApp={handleEnterApp} />;
    }
  };

  if (currentPage === 'welcome') {
    return renderCurrentPage();
  }

  return (
    <div className={`app-container ${darkMode ? 'dark' : ''}`}>
      {/* Sidebar */}
      <div className={`sidebar ${sidebarOpen ? '' : 'closed'}`}>
        <div className="sidebar-header">
          <div className="logo">
            <span className="logo-icon">ğŸ’°</span>
            {sidebarOpen && <span className="logo-text">Finarth</span>}
          </div>
          <button onClick={() => setSidebarOpen(!sidebarOpen)} className="sidebar-toggle">
            {sidebarOpen ? 'â—€' : 'â–¶'}
          </button>
        </div>

        <nav className="sidebar-nav">
          {navigationItems.map(item => (
            <button
              key={item.id}
              onClick={() => setCurrentPage(item.id)}
              className={`nav-item ${currentPage === item.id ? 'active' : ''}`}
            >
              <span className="nav-icon">{item.icon}</span>
              {sidebarOpen && <span className="nav-text">{item.name}</span>}
            </button>
          ))}
        </nav>

        <div className="sidebar-footer">
          <button onClick={handleLogout} className="primary-button danger full-width">
            Logout
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <header className="top-header">
          <h1 className="page-title">{navigationItems.find(item => item.id === currentPage)?.name || 'Dashboard'}</h1>
          <div className="header-right">
            <div>
              Received: <span className="text-positive">${receivedEntries.reduce((sum, entry) => sum + entry.amount, 0).toFixed(2)}</span>
            </div>
            <div>
              Given: <span className="text-negative">${givenEntries.reduce((sum, entry) => sum + entry.amount, 0).toFixed(2)}</span>
            </div>
          </div>
        </header>

        <main className="page-content">
          {renderCurrentPage()}
        </main>
      </div>
    </div>
  );
};

// App router: expose /login and /signup and serve the MainApp for other routes
export default function App() {
  const { user } = useContext(AuthContext);

  return (
    <Routes>
      <Route
        path="/"
        element={user ? <Navigate to="/dashboard" replace /> : <Navigate to="/login" replace />}
      />
      <Route
        path="/login"
        element={!user ? <Login /> : <Navigate to="/dashboard" replace />}
      />
      <Route
        path="/signup"
        element={!user ? <Signup /> : <Navigate to="/dashboard" replace />}
      />
      <Route path="/dashboard" element={user ? <MainApp /> : <Navigate to="/login" replace />} />
      <Route path="/*" element={user ? <MainApp /> : <Navigate to="/login" replace />} />
    </Routes>
  );
}

