// App.js - Complete Self-Contained Finance Application
import React, { useState, useEffect, useContext } from 'react';
import './App.css';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Login from './Components/Login.jsx';
import Signup from './Components/Signup.jsx';
import { AuthContext } from './contexts/AuthContext';

// WelcomePage Component
const WelcomePage = ({ onEnterApp }) => {
  const [isAnimating, setIsAnimating] = useState(false);

  const handleEnterApp = () => {
    setIsAnimating(true);
    setTimeout(() => {
      onEnterApp();
    }, 800);
  };

  return (
    <div className={`welcome-container ${isAnimating ? 'fade-out' : ''}`}>
      <div className="welcome-content">
        <div className="welcome-logo-section">
          <div className="welcome-logo"></div>
          <h1 className="welcome-title">Finarth</h1>
          <p className="welcome-subtitle">Your Complete Personal Finance Management Solution</p>
        </div>

        <button onClick={handleEnterApp} className="btn btn-primary welcome-button">
          Enter Application â†’
        </button>
      </div>
    </div>
  );
};

// FileUploadAnalysis Component
const FileUploadAnalysis = ({ onDataExtracted }) => {
  const [dragActive, setDragActive] = useState(false);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [manualText, setManualText] = useState('');

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFile(e.dataTransfer.files[0]);
    }
  };

  const handleFileInput = (e) => {
    if (e.target.files && e.target.files[0]) {
      handleFile(e.target.files[0]);
    }
  };

  const handleFile = async (file) => {
    setLoading(true);

    // Simulate file processing
    setTimeout(() => {
      const mockAnalysis = {
        transactions: [
          { amount: 1200, description: 'Salary', category: 'Income', type: 'income', date: '2024-01-15' },
          { amount: -300, description: 'Groceries', category: 'Food', type: 'expense', date: '2024-01-10' },
          { amount: -150, description: 'Gas', category: 'Transport', type: 'expense', date: '2024-01-08' }
        ],
        summary: {
          totalIncome: 1200,
          totalExpenses: 450,
          netAmount: 750,
          transactionCount: 3
        }
      };

      setAnalysisResult(mockAnalysis);
      onDataExtracted && onDataExtracted(mockAnalysis);
      setLoading(false);
    }, 2000);
  };

  const analyzeText = () => {
    if (!manualText.trim()) return;

    setLoading(true);
    // Simple text analysis
    const amounts = manualText.match(/\d+\.?\d*/g) || [];
    const transactions = amounts.map((amount, index) => ({
      amount: parseFloat(amount),
      description: `Text entry ${index + 1}`,
      category: 'Other',
      type: 'expense',
      date: new Date().toISOString().split('T')[0]
    }));

    const analysis = {
      transactions,
      summary: {
        totalIncome: 0,
        totalExpenses: transactions.reduce((sum, t) => sum + t.amount, 0),
        netAmount: -transactions.reduce((sum, t) => sum + t.amount, 0),
        transactionCount: transactions.length
      }
    };

    setAnalysisResult(analysis);
    onDataExtracted && onDataExtracted(analysis);
    setLoading(false);
  };

  return (
    <div className="component-card">
      <h2>ğŸ“ File Upload & Analysis</h2>

      <div 
        className={`upload-area ${dragActive ? 'active' : ''}`}
        onDragEnter={(e) => { e.preventDefault(); setDragActive(true); }}
        onDragLeave={(e) => { e.preventDefault(); setDragActive(false); }}
        onDragOver={(e) => e.preventDefault()}
        onDrop={handleDrop}
      >
        <div className="upload-icon">ğŸ“¤</div>
        <p>Drag & drop your files here</p>
        <p className="upload-formats">Supports: PDF, Excel, Word, Text files</p>
        <input 
          type="file" 
          onChange={handleFileInput}
          className="file-input-hidden"
          id="file-input"
          accept=".pdf,.xlsx,.xls,.docx,.txt"
        />
        <label htmlFor="file-input" className="primary-button">
          Choose File
        </label>
      </div>

      <div className="manual-section">
        <h3>Or Enter Text Manually</h3>
        <textarea
          value={manualText}
          onChange={(e) => setManualText(e.target.value)}
          placeholder="Enter your financial data or text to analyze..."
          rows="4"
          className="textarea-input"
        />
        <button onClick={analyzeText} className="btn btn-primary primary-button">
          Analyze Text
        </button>
      </div>

      {loading && (
        <div className="loading-panel">
          <div className="spinner"></div>
          <p>Processing your file...</p>
        </div>
      )}

      {analysisResult && (
        <div className="results-panel">
          <h3>ğŸ“Š Analysis Results</h3>
          <div className="summary-cards">
            <div className="summary-card">
              <h4>ğŸ’° Total Income</h4>
              <p>${analysisResult.summary.totalIncome.toFixed(2)}</p>
            </div>
            <div className="summary-card">
              <h4>ğŸ’¸ Total Expenses</h4>
              <p>${analysisResult.summary.totalExpenses.toFixed(2)}</p>
            </div>
            <div className="summary-card">
              <h4>ğŸ“ˆ Net Amount</h4>
              <p className={analysisResult.summary.netAmount >= 0 ? 'text-positive' : 'text-negative'}>
                ${analysisResult.summary.netAmount.toFixed(2)}
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// MoneyReceivedEntry Component
const MoneyReceivedEntry = ({ onEntriesChange }) => {
  const [formData, setFormData] = useState({
    amount: '',
    source: '',
    reason: '',
    date: new Date().toISOString().split('T')[0],
    category: 'Salary'
  });
  const [entries, setEntries] = useState([]);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem('moneyReceivedEntries');
    if (saved) {
      const parsed = JSON.parse(saved);
      setEntries(parsed);
      onEntriesChange && onEntriesChange(parsed);
    }
  }, [onEntriesChange]);

  useEffect(() => {
    localStorage.setItem('moneyReceivedEntries', JSON.stringify(entries));
    onEntriesChange && onEntriesChange(entries);
  }, [entries, onEntriesChange]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.amount || !formData.source || !formData.reason) {
      alert('Please fill all required fields');
      return;
    }

    const entry = {
      ...formData,
      amount: parseFloat(formData.amount),
      id: Date.now(),
      timestamp: new Date().toISOString()
    };

    setEntries(prev => [entry, ...prev]);
    setFormData({
      amount: '',
      source: '',
      reason: '',
      date: new Date().toISOString().split('T')[0],
      category: 'Salary'
    });
    setShowForm(false);
  };

  const deleteEntry = (id) => {
    if (window.confirm('Delete this entry?')) {
      setEntries(prev => prev.filter(entry => entry.id !== id));
    }
  };

  const totalReceived = entries.reduce((sum, entry) => sum + entry.amount, 0);

  return (
    <div className="component-card">
      <div className="component-header">
        <h2>ğŸ’° Money Received</h2>
        <div className="total-display">
          Total: <span className="big-number text-positive">${totalReceived.toFixed(2)}</span>
        </div>
        <button onClick={() => setShowForm(!showForm)} className="btn btn-primary primary-button">
          {showForm ? 'Cancel' : '+ Add Entry'}
        </button>
      </div>

      {showForm && (
        <form onSubmit={handleSubmit} className="form-panel">
          <div className="form-row">
            <input
              type="number"
              placeholder="Amount"
              value={formData.amount}
              onChange={(e) => setFormData({...formData, amount: e.target.value})}
              className="form-input"
              step="0.01"
              required
            />
            <input
              type="date"
              value={formData.date}
              onChange={(e) => setFormData({...formData, date: e.target.value})}
              className="form-input"
              required
            />
          </div>
          <div className="form-row">
            <input
              type="text"
              placeholder="Source (e.g., ABC Company)"
              value={formData.source}
              onChange={(e) => setFormData({...formData, source: e.target.value})}
              className="form-input"
              required
            />
            <select
              value={formData.category}
              onChange={(e) => setFormData({...formData, category: e.target.value})}
              className="form-input"
            >
              <option value="Salary">Salary</option>
              <option value="Freelance">Freelance</option>
              <option value="Investment">Investment</option>
              <option value="Gift">Gift</option>
              <option value="Other">Other</option>
            </select>
          </div>
          <input
            type="text"
            placeholder="Reason (e.g., Monthly salary)"
            value={formData.reason}
            onChange={(e) => setFormData({...formData, reason: e.target.value})}
            className="form-input"
            required
          />
          <button type="submit" className="btn btn-primary submit-button">Add Entry</button>
        </form>
      )}

      <div className="entries-list">
        {entries.length === 0 ? (
          <p className="no-entries">No entries yet. Add your first income entry!</p>
        ) : (
          entries.map(entry => (
            <div key={entry.id} className="entry-card">
              <div className="entry-header">
                <span className="amount-bold text-positive">${entry.amount.toFixed(2)}</span>
                <span className="muted">{new Date(entry.date).toLocaleDateString()}</span>
              </div>
              <div><strong>From:</strong> {entry.source}</div>
              <div><strong>Reason:</strong> {entry.reason}</div>
              <div><strong>Category:</strong> {entry.category}</div>
              <button 
                onClick={() => deleteEntry(entry.id)}
                className="delete-button"
              >
                Delete
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

// MoneyGivenEntry Component
const MoneyGivenEntry = ({ onEntriesChange }) => {
  const [formData, setFormData] = useState({
    amount: '',
    recipient: '',
    reason: '',
    date: new Date().toISOString().split('T')[0],
    category: 'Food'
  });
  const [entries, setEntries] = useState([]);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem('moneyGivenEntries');
    if (saved) {
      const parsed = JSON.parse(saved);
      setEntries(parsed);
      onEntriesChange && onEntriesChange(parsed);
    }
  }, [onEntriesChange]);

  useEffect(() => {
    localStorage.setItem('moneyGivenEntries', JSON.stringify(entries));
    onEntriesChange && onEntriesChange(entries);
  }, [entries, onEntriesChange]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.amount || !formData.recipient || !formData.reason) {
      alert('Please fill all required fields');
      return;
    }

    const entry = {
      ...formData,
      amount: parseFloat(formData.amount),
      id: Date.now(),
      timestamp: new Date().toISOString()
    };

    setEntries(prev => [entry, ...prev]);
    setFormData({
      amount: '',
      recipient: '',
      reason: '',
      date: new Date().toISOString().split('T')[0],
      category: 'Food'
    });
    setShowForm(false);
  };

  const deleteEntry = (id) => {
    if (window.confirm('Delete this entry?')) {
      setEntries(prev => prev.filter(entry => entry.id !== id));
    }
  };

  const totalGiven = entries.reduce((sum, entry) => sum + entry.amount, 0);

  return (
    <div className="component-card">
      <div className="component-header">
        <h2>ğŸ’¸ Money Given</h2>
        <div className="total-display">
          Total: <span className="big-number text-negative">${totalGiven.toFixed(2)}</span>
        </div>
        <button onClick={() => setShowForm(!showForm)} className="btn btn-primary primary-button">
          {showForm ? 'Cancel' : '+ Add Expense'}
        </button>
      </div>

      {showForm && (
        <form onSubmit={handleSubmit} className="form-panel">
          <div className="form-row">
            <input
              type="number"
              placeholder="Amount"
              value={formData.amount}
              onChange={(e) => setFormData({...formData, amount: e.target.value})}
              className="form-input"
              step="0.01"
              required
            />
            <input
              type="date"
              value={formData.date}
              onChange={(e) => setFormData({...formData, date: e.target.value})}
              className="form-input"
              required
            />
          </div>
          <div className="form-row">
            <input
              type="text"
              placeholder="Recipient (e.g., Walmart, John)"
              value={formData.recipient}
              onChange={(e) => setFormData({...formData, recipient: e.target.value})}
              className="form-input"
              required
            />
            <select
              value={formData.category}
              onChange={(e) => setFormData({...formData, category: e.target.value})}
              className="form-input"
            >
              <option value="Food">Food</option>
              <option value="Transport">Transport</option>
              <option value="Shopping">Shopping</option>
              <option value="Bills">Bills</option>
              <option value="Entertainment">Entertainment</option>
              <option value="Other">Other</option>
            </select>
          </div>
          <input
            type="text"
            placeholder="Reason (e.g., Groceries, Gas)"
            value={formData.reason}
            onChange={(e) => setFormData({...formData, reason: e.target.value})}
            className="form-input"
            required
          />
          <button type="submit" className="btn btn-primary submit-button">Add Expense</button>
        </form>
      )}

      <div className="entries-list">
        {entries.length === 0 ? (
          <p className="no-entries">No expenses yet. Add your first expense!</p>
        ) : (
          entries.map(entry => (
            <div key={entry.id} className="entry-card">
              <div className="entry-header">
                <span className="amount-bold text-negative">${entry.amount.toFixed(2)}</span>
                <span className="muted">{new Date(entry.date).toLocaleDateString()}</span>
              </div>
              <div><strong>To:</strong> {entry.recipient}</div>
              <div><strong>For:</strong> {entry.reason}</div>
              <div><strong>Category:</strong> {entry.category}</div>
              <button 
                onClick={() => deleteEntry(entry.id)}
                className="delete-button"
              >
                Delete
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

// SummaryDashboard Component
const SummaryDashboard = ({ receivedEntries = [], givenEntries = [] }) => {
  const totalReceived = receivedEntries.reduce((sum, entry) => sum + entry.amount, 0);
  const totalGiven = givenEntries.reduce((sum, entry) => sum + entry.amount, 0);
  const netAmount = totalReceived - totalGiven;

  return (
    <div className="component-card">
      <h2>ğŸ“Š Financial Summary</h2>

      <div className="summary-grid">
        <div className="summary-card bg-positive">
          <h3>ğŸ’° Total Received</h3>
          <div className="big-number text-positive">${totalReceived.toFixed(2)}</div>
          <small>{receivedEntries.length} transactions</small>
        </div>

  <div className="summary-card bg-negative">
          <h3>ğŸ’¸ Total Given</h3>
          <div className="big-number text-negative">${totalGiven.toFixed(2)}</div>
          <small>{givenEntries.length} transactions</small>
        </div>

  <div className={`summary-card ${netAmount >= 0 ? 'bg-positive' : 'bg-negative'}`}>
          <h3>ğŸ“ˆ Net Amount</h3>
          <div className={`big-number ${netAmount >= 0 ? 'text-positive' : 'text-negative'}`}>
            ${netAmount.toFixed(2)}
          </div>
          <small>{netAmount >= 0 ? 'Profit' : 'Loss'}</small>
        </div>
      </div>

      <div className="recent-section">
        <h3>Recent Transactions</h3>
        <div className="transactions-list">
          {[...receivedEntries, ...givenEntries]
            .sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp))
            .slice(0, 5)
            .map(entry => (
              <div key={entry.id} className="transaction-item">
                <span className={entry.amount > 0 ? 'text-positive' : 'text-negative'}>
                  {entry.source ? '+' : '-'}${Math.abs(entry.amount).toFixed(2)}
                </span>
                <span className="muted">{entry.source || entry.recipient}</span>
                <span>{entry.reason}</span>
                <span className="muted">{new Date(entry.date).toLocaleDateString()}</span>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

// Other Components (simplified versions)
const LoanManagement = () => (
  <div className="component-card">
    <h2>ğŸ¦ Loan Management</h2>
    <p>Track loans with interest calculations</p>
    <div className="coming-soon">Feature coming soon!</div>
  </div>
);

const DailyExpenses = () => (
  <div className="component-card">
    <h2>âš¡ Daily Expenses</h2>
    <p>Quick expense entry system</p>
    <div className="coming-soon">Feature coming soon!</div>
  </div>
);

const FinancialInsights = ({ receivedEntries = [], givenEntries = [] }) => {
  const totalIncome = receivedEntries.reduce((sum, entry) => sum + entry.amount, 0);
  const totalExpenses = givenEntries.reduce((sum, entry) => sum + entry.amount, 0);
  const savingsRate = totalIncome > 0 ? ((totalIncome - totalExpenses) / totalIncome * 100).toFixed(1) : 0;

  return (
    <div className="component-card">
      <h2>ğŸ’¡ Financial Insights</h2>
      <div className="insights-grid">
        <div className="insight-card">
          <h3>ğŸ’° Savings Rate</h3>
          <div className={`big-number ${savingsRate > 20 ? 'text-positive' : 'text-warning'}`}>
            {savingsRate}%
          </div>
          <p>{savingsRate > 20 ? 'Excellent!' : 'Room for improvement'}</p>
        </div>
        <div className="insight-card">
          <h3>ğŸ“Š Financial Status</h3>
          <div className={`medium-number ${totalIncome > totalExpenses ? 'text-positive' : 'text-negative'}`}>
            {totalIncome > totalExpenses ? 'âœ… Positive' : 'âš ï¸ Negative'}
          </div>
          <p>{totalIncome > totalExpenses ? 'You\'re saving money!' : 'Review your expenses'}</p>
        </div>
      </div>
    </div>
  );
};

const SavingsTracker = ({ receivedEntries = [], givenEntries = [] }) => {
  const netSavings = receivedEntries.reduce((sum, entry) => sum + entry.amount, 0) - 
                   givenEntries.reduce((sum, entry) => sum + entry.amount, 0);

  return (
    <div className="component-card">
      <h2>ğŸ¯ Savings Tracker</h2>
      <div className="savings-status">
        <h3>Current Status</h3>
        <div className="huge-number">
          {netSavings > 0 ? 'ğŸ“ˆ Profit' : netSavings < 0 ? 'ğŸ“‰ Loss' : 'â¡ï¸ Neutral'}
        </div>
        <div className={`big-number ${netSavings >= 0 ? 'text-positive' : 'text-negative'}`}>
          ${Math.abs(netSavings).toFixed(2)}
        </div>
      </div>
    </div>
  );
};

const DataVisualization = ({ receivedEntries = [], givenEntries = [] }) => (
  <div className="component-card">
    <h2>ğŸ“ˆ Data Visualization</h2>
    <p>Charts and graphs for your financial data</p>
    <div className="chart-placeholder">
      ğŸ“Š Interactive charts will be displayed here
    </div>
  </div>
);

const Settings = ({ darkMode, setDarkMode }) => (
  <div className="component-card">
    <h2>âš™ï¸ Settings</h2>
    <div className="setting-item">
      <label>
        <input
          type="checkbox"
          checked={darkMode}
          onChange={(e) => setDarkMode(e.target.checked)}
        />
        Dark Mode
      </label>
    </div>
      <button 
        onClick={() => {
          if (window.confirm('Clear all data?')) {
            localStorage.clear();
            window.location.reload();
          }
        }}
        className="primary-button danger"
      >
        Clear All Data
      </button>
  </div>
);

// Main application UI (kept as MainApp so we can expose /login and /signup routes)
const MainApp = () => {
  const [currentPage, setCurrentPage] = useState('welcome');
  const [receivedEntries, setReceivedEntries] = useState([]);
  const [givenEntries, setGivenEntries] = useState([]);
  const [darkMode, setDarkMode] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  const { logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const navigationItems = [
    { id: 'dashboard', name: 'Dashboard', icon: 'ğŸ“Š' },
    { id: 'file-upload', name: 'File Analysis', icon: 'ğŸ“' },
    { id: 'money-received', name: 'Money Received', icon: 'ğŸ’°' },
    { id: 'money-given', name: 'Money Given', icon: 'ğŸ’¸' },
    { id: 'loan-management', name: 'Loan Management', icon: 'ğŸ¦' },
    { id: 'daily-expenses', name: 'Daily Expenses', icon: 'âš¡' },
    { id: 'financial-insights', name: 'Financial Insights', icon: 'ğŸ’¡' },
    { id: 'savings-tracker', name: 'Savings Tracker', icon: 'ğŸ¯' },
    { id: 'data-visualization', name: 'Charts & Graphs', icon: 'ğŸ“ˆ' },
    { id: 'settings', name: 'Settings', icon: 'âš™ï¸' }
  ];

  const handleEnterApp = () => setCurrentPage('dashboard');

  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'welcome':
        return <WelcomePage onEnterApp={handleEnterApp} />;
      case 'dashboard':
        return <SummaryDashboard receivedEntries={receivedEntries} givenEntries={givenEntries} />;
      case 'file-upload':
        return <FileUploadAnalysis onDataExtracted={() => {}} />;
      case 'money-received':
        return <MoneyReceivedEntry onEntriesChange={setReceivedEntries} />;
      case 'money-given':
        return <MoneyGivenEntry onEntriesChange={setGivenEntries} />;
      case 'loan-management':
        return <LoanManagement />;
      case 'daily-expenses':
        return <DailyExpenses />;
      case 'financial-insights':
        return <FinancialInsights receivedEntries={receivedEntries} givenEntries={givenEntries} />;
      case 'savings-tracker':
        return <SavingsTracker receivedEntries={receivedEntries} givenEntries={givenEntries} />;
      case 'data-visualization':
        return <DataVisualization receivedEntries={receivedEntries} givenEntries={givenEntries} />;
      case 'settings':
        return <Settings darkMode={darkMode} setDarkMode={setDarkMode} />;
      default:
        return <WelcomePage onEnterApp={handleEnterApp} />;
    }
  };

  if (currentPage === 'welcome') {
    return renderCurrentPage();
  }

  return (
    <div className={`app-container ${darkMode ? 'dark' : ''}`}>
      {/* Sidebar */}
      <div className={`sidebar ${sidebarOpen ? '' : 'closed'}`}>
        <div className="sidebar-header">
          <div className="logo">
            <span className="logo-icon">ğŸ’°</span>
            {sidebarOpen && <span className="logo-text">Finarth</span>}
          </div>
          {/* Toggle: show hamburger when closed, close icon when open. Positioned absolutely in CSS so visible when collapsed. */}
          <button
            onClick={() => setSidebarOpen(!sidebarOpen)}
            className="sidebar-toggle"
            aria-label={sidebarOpen ? 'Close sidebar' : 'Open sidebar'}
            title={sidebarOpen ? 'Close sidebar' : 'Open sidebar'}
          >
            {sidebarOpen ? 'âœ•' : 'â˜°'}
          </button>
        </div>

        <nav className="sidebar-nav">
          {navigationItems.map(item => (
            <button
              key={item.id}
              onClick={() => setCurrentPage(item.id)}
              className={`nav-item ${currentPage === item.id ? 'active' : ''}`}
            >
              <span className="nav-icon">{item.icon}</span>
              {sidebarOpen && <span className="nav-text">{item.name}</span>}
            </button>
          ))}
        </nav>

        <div className="sidebar-footer">
          <button onClick={handleLogout} className="btn btn-danger full-width">
            Logout
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <header className="top-header">
          <h1 className="page-title">{navigationItems.find(item => item.id === currentPage)?.name || 'Dashboard'}</h1>
          <div className="header-right">
            <div>
              Received: <span className="text-positive">${receivedEntries.reduce((sum, entry) => sum + entry.amount, 0).toFixed(2)}</span>
            </div>
            <div>
              Given: <span className="text-negative">${givenEntries.reduce((sum, entry) => sum + entry.amount, 0).toFixed(2)}</span>
            </div>
          </div>
        </header>

        <main className="page-content">
          {renderCurrentPage()}
        </main>
      </div>
    </div>
  );
};

// App router: expose /login and /signup and serve the MainApp for other routes
export default function App() {
  const { user } = useContext(AuthContext);

  return (
    <Routes>
      <Route
        path="/"
        element={user ? <Navigate to="/dashboard" replace /> : <Navigate to="/login" replace />}
      />
      <Route
        path="/login"
        element={!user ? <Login /> : <Navigate to="/dashboard" replace />}
      />
      <Route
        path="/signup"
        element={!user ? <Signup /> : <Navigate to="/dashboard" replace />}
      />
      <Route path="/dashboard" element={user ? <MainApp /> : <Navigate to="/login" replace />} />
      <Route path="/*" element={user ? <MainApp /> : <Navigate to="/login" replace />} />
    </Routes>
  );
}

