  <div style={componentStyles.container}>
    <h2>âš™ï¸ Settings</h2>
    <div style={componentStyles.settingItem}>
      <label>
        <input
          type="checkbox"
          checked={darkMode}
          onChange={(e) => setDarkMode(e.target.checked)}
