// Main application UI (kept as MainApp so we can expose /login and /signup routes)
const MainApp = () => {
  const [currentPage, setCurrentPage] = useState('dashboard');
  const [receivedEntries, setReceivedEntries] = useState([]);
  const [givenEntries, setGivenEntries] = useState([]);
  const [darkMode, setDarkMode] = useState(() => {
    try {
      const stored = localStorage.getItem('darkMode');
      return stored ? JSON.parse(stored) : false;
    } catch (e) {
      return false;
    }
  });
  const [sidebarOpen, setSidebarOpen] = useState(true);

  const { logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const navigationItems = [
    { id: 'dashboard', name: 'Dashboard', icon: 'ğŸ“Š' },
    { id: 'file-upload', name: 'File Analysis', icon: 'ğŸ“' },
    { id: 'money-received', name: 'Money Received', icon: 'ğŸ’°' },
    { id: 'money-given', name: 'Money Given', icon: 'ğŸ’¸' },
    { id: 'loan-management', name: 'Loan Management', icon: 'ğŸ¦' },
    { id: 'daily-expenses', name: 'Daily Expenses', icon: 'âš¡' },
    { id: 'financial-insights', name: 'Financial Insights', icon: 'ğŸ’¡' },
    { id: 'savings-tracker', name: 'Savings Tracker', icon: 'ğŸ¯' },
    { id: 'data-visualization', name: 'Charts & Graphs', icon: 'ğŸ“ˆ' },
    { id: 'settings', name: 'Settings', icon: 'âš™ï¸' }
  ];

  const handleEnterApp = () => setCurrentPage('dashboard');

  // When file analysis yields transactions, split into received/given and save
  const handleDataExtracted = (analysis) => {
    if (!analysis || !Array.isArray(analysis.transactions)) return;
    const txns = analysis.transactions;
    const incomes = txns.filter(t => t.amount > 0).map(t => ({
      id: t.id || Date.now() + Math.floor(Math.random()*1000),
      amount: Number(t.amount),
      source: t.description || t.source || 'Imported',
      reason: t.category || t.description || '',
      date: t.date || new Date().toISOString().split('T')[0],
      category: t.category || 'Imported',
      timestamp: t.timestamp || new Date().toISOString()
    }));

    const expenses = txns.filter(t => t.amount <= 0).map(t => ({
      id: t.id || Date.now() + Math.floor(Math.random()*1000),
      amount: Math.abs(Number(t.amount)),
      recipient: t.description || t.recipient || 'Imported',
      reason: t.category || t.description || '',
      date: t.date || new Date().toISOString().split('T')[0],
      category: t.category || 'Imported',
      timestamp: t.timestamp || new Date().toISOString()
    }));

    // Prepend new items so recent ones appear first
    if (incomes.length) setReceivedEntries(prev => [...incomes, ...prev]);
    if (expenses.length) setGivenEntries(prev => [...expenses, ...prev]);
  };

  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'welcome':
        return <WelcomePage onEnterApp={handleEnterApp} />;
      case 'dashboard':
        return <SummaryDashboard receivedEntries={receivedEntries} givenEntries={givenEntries} />;
      case 'file-upload':
        return <FileUploadAnalysis onDataExtracted={handleDataExtracted} />;
      case 'money-received':
        return <MoneyReceivedEntry onEntriesChange={setReceivedEntries} entries={receivedEntries} />;
      case 'money-given':
        return <MoneyGivenEntry onEntriesChange={setGivenEntries} entries={givenEntries} />;
      case 'loan-management':
        return <LoanManagement />;
      case 'daily-expenses':
        return <DailyExpenses />;
      case 'financial-insights':
        return <FinancialInsights receivedEntries={receivedEntries} givenEntries={givenEntries} />;
      case 'savings-tracker':
        return <SavingsTracker receivedEntries={receivedEntries} givenEntries={givenEntries} />;
      case 'data-visualization':
        return <DataVisualization receivedEntries={receivedEntries} givenEntries={givenEntries} />;
      case 'settings':
        return <Settings darkMode={darkMode} setDarkMode={setDarkMode} />;
      default:
        return <WelcomePage onEnterApp={handleEnterApp} />;
    }
  };

  // Start on the dashboard by default. The welcome page can still be
  // navigated to explicitly through the navigation if desired.

  // keep body class in sync so CSS rules using `.dark` or `body.dark-mode` work
  useEffect(() => {
    try {
      // avoid toggling body class so sidebar keeps its original styling;
      // the app container (`.app-container.dark`) controls scoped dark styles
      localStorage.setItem('darkMode', JSON.stringify(darkMode));
    } catch (e) {}
  }, [darkMode]);

  return (
    <div className={`app-container ${darkMode ? 'dark' : ''}`} style={{...appStyles.container, ...(darkMode ? appStyles.dark : {})}}>
  {/* Sidebar */}
  <div className={`sidebar ${sidebarOpen ? '' : 'closed'}`} style={{...appStyles.sidebar, width: sidebarOpen ? '280px' : '70px'}}>
        <div style={appStyles.sidebarHeader}>
          <div style={appStyles.logo}>
            <span style={appStyles.logoIcon}><Link to={'logo.png'}></Link></span>
            {sidebarOpen && <span>Finarth</span>}
          </div>
                 <button 
                   onClick={() => setSidebarOpen(!sidebarOpen)} 
                   style={appStyles.toggleButton}
                 >
                  {/* 3-line hamburger icon (uses currentColor so it matches sidebar text) */}
                   <svg width="18" height="14" viewBox="0 0 18 14" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden>
                     <rect x="1" y="1" width="16" height="2" rx="1" fill="currentColor" />
                     <rect x="1" y="6" width="16" height="2" rx="1" fill="currentColor" />
                     <rect x="1" y="11" width="16" height="2" rx="1" fill="currentColor" />
                   </svg>
               </button>
        </div>

        <nav style={appStyles.nav}>
          {navigationItems.map(item => (
            <button
              key={item.id}
              onClick={() => setCurrentPage(item.id)}
              style={{
                ...appStyles.navItem,
                ...(currentPage === item.id ? appStyles.navItemActive : {})
              }}
            >
              <span>{item.icon}</span>
              {sidebarOpen && <span>{item.name}</span>}
            </button>
          ))}
        </nav>

        <div className="sidebar-footer" style={{padding: '0.75rem'}}>
          <button onClick={handleLogout} style={{
            width: '100%',
            padding: '0.7rem 0.8rem',
            borderRadius: '10px',
            background: '#ef4444',
            color: 'white',
            border: 'none',
            cursor: 'pointer',
            fontWeight: 600
          }}>
            Logout
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div style={{...appStyles.mainContent, marginLeft: sidebarOpen ? '280px' : '70px'}}>
        <header style={appStyles.header}>
          <h1>{navigationItems.find(item => item.id === currentPage)?.name || 'Dashboard'}</h1>
          <div style={appStyles.stats}>
            <div>
              Received: <span style={{ color: 'var(--success-color)' }}>
                ${receivedEntries.reduce((sum, entry) => sum + entry.amount, 0).toFixed(2)}
              </span>
            </div>
            <div>
              Given: <span style={{ color: 'var(--danger-color)' }}>
                ${givenEntries.reduce((sum, entry) => sum + entry.amount, 0).toFixed(2)}
              </span>
            </div>
          </div>
        </header>

        <main style={appStyles.pageContent}>
          {renderCurrentPage()}
        </main>
      </div>
    </div>
  );
};
export default function App() {
  const { user } = useContext(AuthContext);

  return (
    <Routes>
      <Route
        path="/"
        element={user ? <Navigate to="/dashboard" replace /> : <Navigate to="/login" replace />}
      />
      <Route
        path="/login"
        element={!user ? <Login /> : <Navigate to="/dashboard" replace />}
      />
      <Route
        path="/signup"
        element={!user ? <Signup /> : <Navigate to="/dashboard" replace />}
      />
      <Route path="/dashboard" element={user ? <MainApp /> : <Navigate to="/login" replace />} />
      <Route path="/*" element={user ? <MainApp /> : <Navigate to="/login" replace />} />
    </Routes>
  );
}

