import React, { useState, useContext } from 'react'
import { useNavigate } from 'react-router-dom'
import { AuthContext } from '../contexts/AuthContext'

export default function Login(){
  const [email,setEmail] = useState('')
  const [password,setPassword] = useState('')
  const [err,setErr] = useState(null)
  const { login } = useContext(AuthContext)
  const nav = useNavigate()

  const submit = async e =>{
    e.preventDefault()
    try{
      await login(email,password)
      nav('/dashboard')
    }catch(error){ setErr(error?.response?.data?.message || 'Login failed') }
  }

  return (
    <div className="max-w-md mx-auto mt-12 card">
      <h2 className="text-2xl mb-4">Login</h2>
      {err && <div className="text-sm text-red-600 mb-2">{err}</div>}
      <form onSubmit={submit} className="space-y-3">
        <input value={email} onChange={e=>setEmail(e.target.value)} placeholder="Email" className="w-full p-3 rounded border" />
        <input value={password} onChange={e=>setPassword(e.target.value)} type="password" placeholder="Password" className="w-full p-3 rounded border" />
        <button className="w-full p-3 rounded-2xl bg-indigo-600 text-white">Sign in</button>
      </form>
    </div>
  )
}
